  [CmdletBinding()]
  param([string[]]$ComputerName, [hashtable]$Capability)

  $bag = @{}
  foreach ($c in $ComputerName) {
    try {
      Write-Log Info "Collecting &lt;Area&gt; on $c"
      # prefer modern module
      if ($Capability.Has&lt;Module&gt;) {
        # use module cmdlets
        $bag[$c] = @{ ... }
      } else {
        # fallback (CIM/WMI/registry/netsh)
        $bag[$c] = @{ ... }
      }
    } catch {
      $bag[$c] = @{ Error = $_.Exception.Message }
      Write-Log Error "Collector &lt;Area&gt; failed on $c : $($_.Exception.Message)"
    }
  }
  return $bag
}
