  [CmdletBinding()]
  param([hashtable]$Data,[string]$OutDir,[string]$Timestamp)

  $findings = @()

  # Examples of “ready/not-ready” heuristics
  foreach($srv in $Data['Get-SATRolesFeatures'].Keys){
    $roles = $Data['Get-SATRolesFeatures'][$srv]['Installed']
    if($roles -contains 'DHCP'){
      $dhcp = $Data['Get-SATDHCP'][$srv]
      if(($dhcp.Leases -as [int]) -gt 0){ $findings += "[$srv] DHCP migration required: $($dhcp.Scopes.Count) scopes." }
    }
    if($roles -contains 'DNS'){
      $zones = $Data['Get-SATDNS'][$srv].Zones
      if($zones.Count -gt 0){ $findings += "[$srv] DNS migration required: $($zones.Count) zones." }
    }
  }

  $summary = @"
# ServerAuditToolkitV2 Migration Readiness

Run: $Timestamp

## High-level findings
$(($findings | ForEach-Object { "* $_" }) -join "`n")

## Inventory footprint
- Servers: $(@($Data['Get-SATSystem'].Keys).Count)
- Roles/Features inventoried: yes
- DHCP/DNS/IIS/Hyper-V captured: see JSON/HTML for details
"@
  $md = Join-Path $OutDir "summary_$Timestamp.md"
  $html = Join-Path $OutDir "report_$Timestamp.html"
  $summary | Set-Content -Path $md -Encoding UTF8
  ($summary | ConvertTo-Html -Title "SATV2 Report $Timestamp") | Set-Content -Path $html -Encoding UTF8
  return @{ Markdown=$md; Html=$html }
}
