  It 'returns hashtable for localhost' {
    $cap = Get-SATCapability
    $r = Get-SATSMB -ComputerName $env:COMPUTERNAME -Capability $cap -MaxAclEntries 1
    $r | Should -BeOfType Hashtable
  }
}

Describe 'Certificates collector' {
  It 'returns hashtable for localhost' {
    $cap = Get-SATCapability
    $r = Get-SATCertificates -ComputerName $env:COMPUTERNAME -Capability $cap
    $r | Should -BeOfType Hashtable
  }
}

Describe 'ADDS collector' {
  It 'returns hashtable' {
    $cap = Get-SATCapability
    $r = Get-SATADDS -ComputerName $env:COMPUTERNAME -Capability $cap
    $r | Should -BeOfType Hashtable
  }
}
</span></code></div></div></pre>
<hr data-start="16440" data-end="16443">
<h2 data-start="16445" data-end="16464">What you get now</h2>
<ul data-start="16466" data-end="16934">
<li data-start="16466" data-end="16628">
<p data-start="16468" data-end="16628"><strong data-start="16468" data-end="16475">SMB</strong>: exact share list + capped top-level NTFS ACL snapshot for each share → fast inventory that’s migration-useful (you can expand to recurse where needed).</p>
</li>
<li data-start="16629" data-end="16806">
<p data-start="16631" data-end="16806"><strong data-start="16631" data-end="16647">Certificates</strong>: all key machine stores with expiry → flags expiring certs and helps map IIS HTTPS bindings (via <code data-start="16745" data-end="16770">netsh http show sslcert</code> already captured by IIS collector).</p>
</li>
<li data-start="16807" data-end="16934">
<p data-start="16809" data-end="16934"><strong data-start="16809" data-end="16818">AD DS</strong>: forest/domain levels, DCs, FSMOs, and a heuristic for SYSVOL replication type → highlights pre-migration blockers.</p>
</li>
</ul>
<p data-start="16936" data-end="16964">If you want, I can also add:</p>
<ul data-start="16965" data-end="17223">
<li data-start="16965" data-end="17063">
<p data-start="16967" data-end="17063">Share→dependency cross-refs (e.g., IIS sites pointing to share paths, Hyper-V VM storage paths).</p>
</li>
<li data-start="17064" data-end="17134">
<p data-start="17066" data-end="17134">Optional deep ACL crawl with throttling and path allow/deny filters.</p>
</li>
<li data-start="17135" data-end="17223">
<p data-start="17137" data-end="17223">A toggle to emit a single self-contained HTML (Bootstrap CSS inlined + JSON embedded).</p>
</li>
</ul>
