051 6.01057 5.53708 6.00551 6.00391 6.00391C6.00551 5.53708 6.01057 5.14051 6.03809 4.80371C6.0755 4.34588 6.15483 3.94012 6.34668 3.56348L6.46875 3.34473C6.77282 2.84912 7.20856 2.44514 7.72949 2.17969L7.87207 2.11328C8.20855 1.96886 8.56979 1.90385 8.9707 1.87109C9.42091 1.83432 9.978 1.83496 10.667 1.83496H13.5C14.1891 1.83496 14.746 1.83431 15.1963 1.87109C15.6541 1.90851 16.0599 1.98788 16.4365 2.17969L16.6553 2.30176C17.151 2.60585 17.5548 3.04244 17.8203 3.56348L17.8867 3.70606C18.031 4.04235 18.0962 4.40306 18.1289 4.80371C18.1657 5.25395 18.165 5.81091 18.165 6.5V9.33301Z"></path></svg>Copy code</button></div></div></div><div class="overflow-y-auto p-4" dir="ltr"><code class="whitespace-pre! language-powershell"><span>function New-SATMigrationUnits {
  param([hashtable]$Data)

  $units = New-Object System.Collections.Generic.List[hashtable]

  # DHCP scopes → DhcpScope
  foreach($srv in @($Data['Get-SATDHCP'].Keys)){
    foreach($s in @($Data['Get-SATDHCP'][$srv].Scopes)){
      $cidr = "$($s.StartRange)-$($s.EndRange)/$($s.SubnetMask)"
      $units.Add(@{
        Id="dhcp:$srv:scope:$($s.ScopeId)"
        Kind="DhcpScope"; Server=$srv; Name=$s.Name
        Summary="Scope $($s.ScopeId) $cidr ($($s.State))"
        DependsOn=@(); SizeHint=$null
        Evidence=@(@{ Collector='Get-SATDHCP'; Keys=@('ScopeId','Name','StartRange','EndRange','SubnetMask') })
        Confidence=1.0
      })
    }
  }

  # IIS bindings → link to certs by thumbprint
  foreach($srv in @($Data['Get-SATIIS'].Keys)){
    foreach($site in @($Data['Get-SATIIS'][$srv].Sites)){
      $siteId = "iis:$srv:site:$($site.Name)"
      $units.Add(@{ Id=$siteId; Kind='IisSite'; Server=$srv; Name=$site.Name
        Summary="IIS site $($site.State) ($($site.PhysicalPath))"
        DependsOn=@("iispool:$srv:$($site.AppPool)"); Evidence=@(@{Collector='Get-SATIIS'; Keys=@('Name','Bindings','AppPool')}); Confidence=1.0 })
      foreach($b in @($site.Bindings)){
        $thumb = ($b.certificateHash ?? $b.thumbprint)
        $dep = @()
        if($thumb){ $dep += "cert:$srv:$thumb" }
        $units.Add(@{ Id="iis:$srv:binding:$($site.Name):$($b.protocol):$($b.bindingInformation)"
          Kind='IisBinding'; Server=$srv; Name="$($b.protocol) $($b.bindingInformation)"
          Summary="Binding on $($site.Name)"; DependsOn=$dep
          Evidence=@(@{Collector='Get-SATIIS'; Keys=@('protocol','bindingInformation')}); Confidence=1.0 })
      }
      # Pool
      $units.Add(@{ Id="iispool:$srv:$($site.AppPool)"; Kind='IisAppPool'; Server=$srv; Name=$site.AppPool
        Summary='IIS AppPool'; DependsOn=@(); Evidence=@(@{Collector='Get-SATIIS'; Keys=@('AppPool')}); Confidence=1.0 })
    }
  }

  # Certs → Certificate
  foreach($srv in @($Data['Get-SATCertificates'].Keys)){
    foreach($store in @($Data['Get-SATCertificates'][$srv].Stores.Keys)){
      foreach($c in @($Data['Get-SATCertificates'][$srv].Stores[$store])){
        $units.Add(@{ Id="cert:$srv:$($c.Thumbprint)"; Kind='Certificate'; Server=$srv
          Name=$c.FriendlyName; Summary="$store NotAfter=$($c.NotAfter)"; DependsOn=@()
          Evidence=@(@{Collector='Get-SATCertificates'; Keys=@('Thumbprint','Subject','NotAfter')})
          Confidence=1.0 })
      }
    }
  }

  return $units
}
</span></code></div></div></pre>
