function Get-SATPSMajor { if ($PSVersionTable) { $PSVersionTable.PSVersion.Major } else { 2 } }

# --- new-object wrapper: [pscustomobject] for PS3+, PSObject otherwise ---
function New-SATObject {
  param([hashtable]$Properties)
  if ((Get-SATPSMajor) -ge 3) { return [pscustomobject]$Properties }
  $o = New-Object PSObject
  foreach ($k in $Properties.Keys) { Add-Member -InputObject $o -NotePropertyName $k -NotePropertyValue $Properties[$k] }
  return $o
}

# --- test module availability without throwing ---
function Test-SATModule {
  param([string]$Name)
  $m = Get-Module -ListAvailable -Name $Name -ErrorAction SilentlyContinue
  return [bool]$m
}

# --- safe Get-Service status (no ?. operator) ---
function Get-SATServiceStatus {
  param([string]$Name)
  $svc = Get-Service -Name $Name -ErrorAction SilentlyContinue
  if ($svc) { return $svc.Status } else { return $null }
}

# --- JSON/CLIXML export: JSON only where available (PS3+) ---
function Export-SATData {
  param([Parameter(Mandatory)]$Object,[Parameter(Mandatory)][string]$PathBase,[int]$Depth=6)
  $jsonCmd = Get-Command -Name ConvertTo-Json -ErrorAction SilentlyContinue
  if ($jsonCmd) {
    $jsonPath = "$PathBase.json"
    $Object | ConvertTo-Json -Depth $Depth | Set-Content -Encoding UTF8 -Path $jsonPath
    return $jsonPath
  } else {
    $xmlPath = "$PathBase.clixml"
    $Object | Export-Clixml -Path $xmlPath
    return $xmlPath
  }
}
</span></code></div></div></pre>
